#define ll long long
const ll INF=100000000000;
class Solution {
public:
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector< pair<int,int> > adj[n];
        vector< pair<int,int> > rev[n];
        for(auto e: edges){
            adj[e[0]].push_back({e[1],e[2]});
            rev[e[1]].push_back({e[0],e[2]});
        }
		//distance of all nodes from src1
        vector<ll> dist1(n,INF);
        dijkstra(adj,src1,dist1);

		//distance of all nodes from src2
        vector<ll> dist2(n,INF);
        dijkstra(adj,src2,dist2);

		//distance of all nodes from dest
        vector<ll> dist3(n,INF);
        dijkstra(rev,dest,dist3);

        ll ans=INF;
        for(int i=0;i<n;i++){
            ans=min(ans,dist1[i]+dist2[i]+dist3[i]);
        }
        if(ans==INF)ans=-1;
        return ans;
    }
    void dijkstra(vector< pair<int,int> > adj[],int s,vector<ll> &dist){
        priority_queue< pair<ll,int> , vector< pair<ll,int> > ,greater< pair<ll,int> > > pq;
        pq.push({0,s});
        dist[s]=0;

        while(!pq.empty()){
            int u=pq.top().second;
            ll d=pq.top().first;
            pq.pop();

            if(d!=dist[u])continue;

            for(auto [v,w]: adj[u]){
                if(dist[v] >= d+w){
                    dist[v]=d+w;
                    pq.push({dist[v],v});
                }
            }
        }
    }
};
