class Solution {
public:
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector<vector<pair<int, int>>> graph(n), trans(n); 
        for (auto& edge : edges) {
            graph[edge[0]].push_back({edge[1], edge[2]});
            trans[edge[1]].push_back({edge[0], edge[2]}); 
        } 

        auto bfs = [&](int x, vector<vector<pair<int, int>>>& graph) {
            vector<long long> dist(n, -1); 
            queue<pair<int, long long>> q; q.emplace(x, 0); 
            dist[x] = 0; 
            while (q.size()) {
                auto [u, w] = q.front(); q.pop(); 
                if (dist[u] == w) 
                    for (auto& [v, ww] : graph[u]) 
                        if (dist[v] == -1 || w+ww < dist[v]) {
                            q.emplace(v, w+ww); 
                            dist[v] = w+ww; 
                        }
            }
            return dist; 
        };

        vector<long long> dsrc1 = bfs(src1, graph), dsrc2 = bfs(src2, graph), ddest = bfs(dest, trans); 
        long long ans = -1; 
        for (int i = 0; i < n; ++i) {
            if (dsrc1[i] != -1 && dsrc2[i] != -1 && ddest[i] != -1 && (ans == -1 || dsrc1[i] + dsrc2[i] + ddest[i] < ans))
                ans = dsrc1[i] + dsrc2[i] + ddest[i]; 
        }
        return ans; 
    }
};
