class Solution {
public:
    void prepare(unordered_map<int, set<pair<int, int>>> &adj, vector<vector<int>> &edges)
    {
        for(int i = 0 ; i < edges.size() ; i++)
        {
            adj[edges[i][0]].insert({edges[i][1], edges[i][2]});
        }
    }
    void dijkstras(int src, unordered_map<int, set<pair<int, int>>> &adj, vector<long long> &dist)
    {
        dist[src] = 0;
        priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<pair<long long, int>>> pq;
        pq.push({0, src});

        while(!pq.empty())
        {
            pair<long long, int> t = pq.top();
            pq.pop();
            long long d = t.first;
            int node = t.second;

            for(auto it: adj[node])
            {
                if(d + it.second < dist[it.first])
                {
                    dist[it.first] = d + it.second;
                    pq.push({d+it.second, it.first});
                }
            }
        }
    }
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector<vector<int>> rev_edges;
        for(int i = 0 ; i < edges.size() ; i++)
        {
            rev_edges.push_back({edges[i][1], edges[i][0], edges[i][2]});
        }

        unordered_map<int, set<pair<int, int>>> adj;
        unordered_map<int, set<pair<int, int>>> adj_rev;

        prepare(adj, edges);
        prepare(adj_rev, rev_edges);

        vector<long long> dist_s1(n, LLONG_MAX);
        vector<long long> dist_s2(n, LLONG_MAX);
        vector<long long> dist_des(n, LLONG_MAX);
        dijkstras(src1, adj, dist_s1);
        dijkstras(src2, adj, dist_s2);
        dijkstras(dest, adj_rev, dist_des);


        //src1 -> src2 -> dest
        long long path1 = LLONG_MAX;
        if(dist_s1[src2] != LLONG_MAX && dist_s2[dest] != LLONG_MAX)
            path1 = dist_s1[src2] + dist_s2[dest];

        //src2 -> src1 -> dest
        long long path2 = LLONG_MAX;
        if(dist_s2[src1] != LLONG_MAX && dist_s1[dest] != LLONG_MAX)
            path2 = dist_s2[src1] + dist_s1[dest];


        // dest -> 'x' -> src1 and src2 (x is any intermediate node apart from src1 and src2)
        long long path3 = LLONG_MAX;
        for(int i = 0 ; i < n ; i++)
        {
            if(i != src1 && i != src2)
            {
                if(dist_des[i] != LLONG_MAX && dist_s1[i] != LLONG_MAX && dist_s2[i] != LLONG_MAX)
                {
                    path3 = min(path3, dist_des[i] + dist_s1[i] + dist_s2[i]);
                }
            }
        }

        if(path1 != LLONG_MAX || path2 != LLONG_MAX || path3 != LLONG_MAX)
        {
            return min(path1, min(path2, path3));
        }
        else 
            return -1;
    }
};
```

