```
/**
 * some observations
 * 1. the minimum weight of a subgraph of the graph descripted by the edges `edges` is
 *    the minimum weight of the edges of the two paths,
 *    one path is from the node `src1` to the node `dest`,
 *    another path is from the node `scr2` to the node `dest`
 * 2. there must exist a node `node` such that
 *    the sum of the weights of the following three paths is minimum,
 *    these three paths constitue the target subgraph
 *    1). the minimum weight of the edges of the path from the node `src1` to the node `node`
 *    2). the minimum weight of the edges of the path from the node `src2` to the node `node`
 *    3). the minimum weight of the edges of the path from the node `node` to the node `dest`
 *    `src1`
 *          \
 *           `node`--`dest`
 *          /
 *    `src2`
 *    please note that the node `node` could be the node `src1`,
 *                     or the node `scr2`, or the node `dest`
 *
 * Time Complexity: (n + n_edges * log(n))
 * Space Complexity: O(n + n_edges)
 * where `n_edges` is the length of the vector `edges`
 */
class Solution {
 public:
  long long minimumWeight(const int n,
                          const vector<vector<int>> &edges,
                          const int src1,
                          const int src2,
                          const int dest) {
    constexpr int from_i = 0;
    constexpr int to_i = 1;
    constexpr int weight_i = 2;
    vector<pair<int, int>> graph[n];
    vector<pair<int, int>> reversed_graph[n];
    for (const vector<int> &edge : edges) {
      graph[edge[from_i]].emplace_back(edge[to_i], edge[weight_i]);
      reversed_graph[edge[to_i]].emplace_back(edge[from_i], edge[weight_i]);
    }

    long long distances_src1[n];
    memset(distances_src1, -1, sizeof(distances_src1));
    dijkstra(graph, n, src1, distances_src1);
    long long distances_src2[n];
    memset(distances_src2, -1, sizeof(distances_src2));
    dijkstra(graph, n, src2, distances_src2);
    long long distances_dest[n];
    memset(distances_dest, -1, sizeof(distances_dest));
    dijkstra(reversed_graph, n, dest, distances_dest);

    long long ret = numeric_limits<long long>::max();
    for (int node = 0; node < n; ++node) {
      if (distances_src1[node] != -1 && distances_src2[node] != -1 && distances_dest[node] != -1) {
        ret = min(ret, distances_src1[node] + distances_src2[node] + distances_dest[node]);
      }
    }
    return ret == numeric_limits<long long>::max() ? -1 : ret;;
  }

 private:
  void dijkstra(const vector<pair<int, int>> *graph, const int n, const int node, long long *distances) {
    distances[node] = 0LL;
    bool visited[n];
    memset(visited, 0, sizeof(visited));
    priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<>> pq;
    pq.emplace(0LL, node);
    while (!pq.empty()) {
      const auto [distance, node] = pq.top();
      pq.pop();
      if (visited[node]) {
        continue;
      }
      visited[node] = true;
      distances[node] = distance;
      for (const auto [next, distance_delta] : graph[node]) {
        if (!visited[next]) {
          pq.emplace(distance + distance_delta, next);
        }
      }
    }
  }
};
```

