using ti = pair<long long, long long>;
class Solution {
public:
    void dijkstra(vector<vector<ti>>& adj, vector<long long>& ans, int src) {
        priority_queue< ti, vector<ti>, greater<>> pq;
        pq.push({ 0, src });
        while (!pq.empty()) {
            auto [w, node] = pq.top();
            pq.pop();
            if (ans[node] != -1) continue;
            ans[node] = w;
            for (auto &[nei, neiW]: adj[node]) pq.push({ w+neiW, nei });
        }
    }

    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector<long long> time1(n, -1);
        vector<long long> time2(n, -1);
        vector<long long> time3(n, -1);

        vector<vector<ti>> adj(n);
        vector<vector<ti>> adjR(n);

        for (auto& e: edges) {
            adj[e[0]].push_back({ e[1], e[2] });
            adjR[e[1]].push_back({ e[0], e[2] });
        }

        dijkstra(adj, time1, src1);
        dijkstra(adj, time2, src2);
        dijkstra(adjR, time3, dest);

        long long ans = LONG_LONG_MAX;
        for (int i = 0; i < n; i++) {
            if (time1[i] == -1 || time2[i] == -1 || time3[i] == -1) continue;
            ans = min(ans, time1[i] + time2[i] + time3[i]);
        }

        return ans == LONG_LONG_MAX ? -1 : ans;
    }
};

