             weight=mp.begin()->first;
             node=mp.begin()->second;
             mp.erase(mp.begin());
             if(v1[node]==-1)
             v1[node]=weight;
             auto itr=e[node].begin();
             while(itr!=e[node].end())
             {
                 if(v1[itr->first]==-1)
