class Solution {
public:
    long long minimumWeight(const int& N, vector<vector<int>>& edges, int src1, int src2, int dest) {
        Graph G(N), rev_G(N);
        static const long long inf = LONG_MAX;
        long long ans = inf;


        for (const vector<int>& E : edges)
            G[E[0]].push_back(make_pair(E[1], E[2])), rev_G[E[1]].push_back(make_pair(E[0], E[2]));

        vector<long long> dist1(N, inf), dist2(N, inf), dist3(N, inf);
        Dijkstra(G, src1, dist1);
        Dijkstra(G, src2, dist2);
        Dijkstra(rev_G, dest, dist3);



        for (int i = 0; i < N; i++)
            if (dist3[i] != inf && dist1[i] != inf && dist2[i] != inf)
                ans = min(ans, dist3[i] + dist1[i] + dist2[i]);

        return ans == inf ? -1 : ans;
    }

private:
    using Graph = vector<vector<pair<int, int> > >;

    void Dijkstra(const Graph& G, const int& src, vector<long long>& dist)
    {
        dist[src] = 0;
        priority_queue<pair<long long, long long>, vector<pair<long long, long long>>, greater<pair<long long, long long>>> q;
        q.push(make_pair(0, src));

        while (q.empty() == false)
        {
            auto [d, u] = q.top();
            q.pop();

            if (d > dist[u]) continue; // Avoiding for getting TLE

            for (const pair<int, int>& E : G[u])
                if (dist[E.first] > E.second + d)
                    dist[E.first] = d + E.second,  q.push(make_pair(E.second + d, E.first));
        }

    }
};
