def dijkstra(self, n, dict1, s1):
    visited, stack = defaultdict(int), [(0,s1)]

    while stack:
        time, node = heappop(stack)

        if node not in visited:
            visited[node] = time

            for neighbor in dict1[node]:
                if neighbor[0] not in visited:
                    heappush(stack, (time + neighbor[1], neighbor[0]))

    return [visited.get(i,float("inf")) for i in range(n)]

def minimumWeight(self, n, edges, src1, src2, dest):
    dict1, dict2, min_val = defaultdict(list), defaultdict(list), float("inf")

    for i, j, k in edges:
        dict1[i].append((j,k))
        dict2[j].append((i,k))

    d1 = self.dijkstra(n,dict1,src1)
    d2 = self.dijkstra(n,dict1,src2)
    d3 = self.dijkstra(n,dict2,dest)


    for i in range(n):
        min_val = min(min_val, d1[i] + d2[i] + d3[i])

    return min_val if min_val != float("inf") else -1

