function minimumWeight(n, edges, src1, src2, dest) {

    const list = [];
    const reverseList = [];

    for (let i = 0; i < n; i++) {
        list[i] = [];
        reverseList[i] = [];
    }

    for (const [a, b, w] of edges) {
        list[a].push([b, w]);
        reverseList[b].push([a, w]);
    }

    function bfs(node, reverse, sources) {
        const minPaths = new Map();
        const heap = new MinPriorityQueue();

        heap.enqueue(node, 0);

        while (!heap.isEmpty()) {
            const next = heap.dequeue();
            const path = minPaths.get(next.element);

            if (path !== undefined) continue;

            minPaths.set(next.element, next.priority);

            const adjacencies = reverse ? reverseList[next.element] : list[next.element];

            for (const [node, weight] of adjacencies) {
                if (reverse || sources.has(node)) {
                    heap.enqueue(node, next.priority + weight);
                }
            }
        }

        return minPaths;
    }

    const minDest = bfs(dest, true);

    if (!minDest.has(src1)) return -1;
    if (!minDest.has(src2)) return -1;

    const minSrc1 = bfs(src1, false, minDest);
    const minSrc2 = bfs(src2, false, minDest);

    let min = Number.MAX_SAFE_INTEGER;

    for (const [midpoint, pathSum] of minDest) {
        const path1 = minSrc1.get(midpoint);
        const path2 = minSrc2.get(midpoint);

        if (path1 === undefined || path2 === undefined) continue;

        const sum = path1 + path2 + minDest.get(midpoint);

        if (sum < min) min = sum;
    }

    return min;
}
