def minimumWeight(self, n, edges, src1, src2, dest):
    def spf(src, g):
        q, dist, = [(0, src)], [math.inf] * n
        while q:
            d, u = heappop(q)
            if dist[u] < math.inf: continue
            dist[u] = d
            for v, w in g[u]: heappush(q, (d + w, v))
        return dist

    G = [[[] for _ in range(n)] for _ in range(2)]
    for u, v, w in edges:
        for i, (u, v) in enumerate([(u, v), (v, u)]):
            G[i][u].append((v, w))
    res = min(map(sum, zip(*itertools.starmap(spf, [(src1, G[0]), (src2, G[0]), (dest, G[1])]))))
    return res if res < math.inf else -1
