type gNode struct{ v, w int }
type qNode struct {
	d int64
	u int
}

type queue []*qNode

func (q queue) Len() int            { return len(q) }
func (q queue) Less(i, j int) bool  { return q[i].d < q[j].d }
func (q queue) Swap(i, j int)       { q[i], q[j] = q[j], q[i] }
func (q *queue) Push(x interface{}) { *q = append(*q, x.(*qNode)) }
func (q *queue) Pop() interface{} {
	i := len(*q) - 1
	x := (*q)[i]
	*q = (*q)[:i]
	return x
}

func spf(src int, g [][]*gNode, dist []int64) {
	var q queue
	heap.Push(&q, &qNode{int64(0), src})
	for q.Len() > 0 {
		x := heap.Pop(&q).(*qNode)
		if dist[x.u] == -1 {
			dist[x.u] = x.d
			for _, y := range g[x.u] {
				heap.Push(&q, &qNode{x.d + int64(y.w), y.v})
			}
		}
	}
}

func minimumWeight(n int, edges [][]int, src1 int, src2 int, dest int) int64 {
	var (
		res = int64(-1)
		G   [2][][]*gNode
		D   [3][]int64
	)
	for i := range G {
		G[i] = make([][]*gNode, n)
	}
	for _, e := range edges {
		u, v, w := e[0], e[1], e[2]
		for i, x := range [2][2]int{[2]int{u, v}, [2]int{v, u}} {
			u, v = x[0], x[1]
			G[i][u] = append(G[i][u], &gNode{v, w})
		}
	}
	for i := range D {
		D[i] = make([]int64, n)
		for j := range D[i] {
			D[i][j] = -1
		}
	}
	spf(src1, G[0], D[0])
	spf(src2, G[0], D[1])
	spf(dest, G[1], D[2])
	for i := 0; i < n; i++ {
		if D[0][i] != -1 && D[1][i] != -1 && D[2][i] != -1 {
			if x := D[0][i] + D[1][i] + D[2][i]; res == -1 || x < res {
				res = x
			}
		}
	}
	return res
}
