class Solution {
public:

    vector<long long> Cal(vector<vector<vector<int>>>& Graph, int start, int N){
        vector<long long> Dis(N, LLONG_MAX);

        priority_queue < vector<long long>, vector<vector<long long>>, greater<vector<long long>> > Q;

        Q.push({0,start});
        Dis[start] = 0;

        while(!Q.empty()){
            vector<long long> Vec = Q.top();
            Q.pop();

            long long d = Vec[0];
            int node = Vec[1];

            if(d > Dis[node]) continue;

            for(int i=0;i<Graph[node].size();i++){
                int next = Graph[node][i][0];
                long long w = Graph[node][i][1];

                if(d+w < Dis[next]){
                    Dis[next] = d+w;
                    Q.push({Dis[next],next});
                }
            }
        }

        return Dis;
    }

    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector<vector<vector<int>>> Graph(n);
        vector<vector<vector<int>>> RevGraph(n);

        for(int i=0;i<edges.size();i++){
            int u = edges[i][0];
            int v = edges[i][1];
            int w = edges[i][2];

            Graph[u].push_back({v,w});
            RevGraph[v].push_back({u,w});
        }

        vector<long long> Dis1 = Cal(Graph, src1, n);
        vector<long long> Dis2 = Cal(Graph, src2, n);
        vector<long long> Dis3 = Cal(RevGraph, dest, n);

        long long Ans = LLONG_MAX;
        for(int i=0;i<n;i++){
            if(Dis1[i]!=LLONG_MAX && Dis2[i]!=LLONG_MAX && Dis3[i]!=LLONG_MAX){
                Ans = min(Ans, Dis1[i]+Dis2[i]+Dis3[i]);
            }
        }

        if(Ans == LLONG_MAX) return -1;
        return Ans;
    }
};
