typedef pair<long long, int> p;
class Solution {
public:
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {

        // Create Adjacency List
        vector<vector<pair<int, long long>>>adj(n),rev(n);
        for(int i=0;i<edges.size();i++){
            adj[edges[i][0]].push_back({edges[i][1],edges[i][2]});
            rev[edges[i][1]].push_back({edges[i][0],edges[i][2]});
        }

        vector<long long >dis_src1=dk(adj,src1);
        vector<long long >dis_src2=dk(adj,src2);
        vector<long long >dis_dest=dk(rev,dest);

        long long ans=1e18;
        for(int i=0;i<n;i++)
            ans=min(ans, dis_src1[i]+dis_src2[i]+dis_dest[i]);
        return ans==1e18?-1:ans;
    }

    vector<long long> dk(vector<vector<pair<int,long long>>>adj, int src){
        vector<long long>dis(adj.size(),1e18);

        // Create a Min Heap sorted on distance
        priority_queue<p, vector<p> ,greater<p> >pq;
        dis[src]=0;
        pq.push({0,src});

        while(!pq.empty()){
            p tmp=pq.top();
            pq.pop();
            int u=tmp.second;

            // To remove redundant path which are not optimal
            if (tmp.first != dis[u]) continue;

            for(int i=0;i<adj[u].size();i++){
                int v=adj[u][i].first;
                long long w=adj[u][i].second;
                if(dis[v] > (dis[u]+w)){
                    dis[v]=dis[u]+w;
                    pq.push({dis[v],v});
                }
            }
        }
        return dis;
    }
};
