class Solution {
public:
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector<int> xr(n, 0);
        vector<int> reachable;
        unordered_map<int, vector<pair<int,int>>> graph, revGraph;//<node-><node, dist>>
        vector<unsigned long long> dist1(n, LLONG_MAX), dist2(n, LLONG_MAX), dist3(n, LLONG_MAX);
        auto comp=[](const pair<int,unsigned long long> &a, const pair<int,unsigned long long> &b){
            return a.second>b.second;
        };
        priority_queue<pair<int,unsigned long long>, vector<pair<int,unsigned long long>>, decltype(comp)> pq(comp);//<node, dist>
        for(auto &e: edges){
            int a=e[0], b=e[1], dist=e[2];
            graph[a].push_back({b, dist});
            revGraph[b].push_back({a, dist});            
        }
        dist1[src1]=0;
        pq.push({src1,0});
        while(!pq.empty()){
            auto [node, d]=pq.top();
            pq.pop(); 
            if (dist1[node] != d)
                continue;
            for(auto [child, childDist]: graph[node]){
                if(d+childDist<dist1[child]){ 
                    dist1[child]=d+childDist;
                    pq.push({child, dist1[child]});
                }
            }
        }
        dist2[src2]=0;
        pq.push({src2, 0});
        while(!pq.empty()){
            auto [node, d]=pq.top();
            pq.pop(); 
            if (dist2[node] != d)
                continue;
            for(auto [child, childDist]: graph[node]){
                if(d+childDist<dist2[child]){ 
                    dist2[child]=d+childDist;
                    pq.push({child, dist2[child]});
                }
            }
        }
        dist3[dest]=0;
        pq.push({dest, 0});
        while(!pq.empty()){
            auto [node, d]=pq.top();
            if(dist1[node]!=LLONG_MAX && dist1[node]!=LLONG_MAX)
                reachable.push_back(node);
            pq.pop();
            if (dist3[node] != d)
                continue;
            for(auto [child, childDist]: revGraph[node]){
                if(d+childDist<dist3[child]){ 
                    dist3[child]=d+childDist;
                    pq.push({child, dist3[child]});
                }
            }
        }
        unsigned long long ans=ULLONG_MAX;
        for(auto &i: reachable){
            if(dist1[i]==LLONG_MAX || dist2[i]==LLONG_MAX || dist3[i]==LLONG_MAX)
                continue;
            ans=min(ans, (unsigned long long)dist1[i]+(unsigned long long)dist2[i]+(unsigned long long)dist3[i]);
        }
        return ans!=ULLONG_MAX?ans:-1;
    }
