class Solution {
public:
    const long long INF = 1e16 + 7;
    vector<long long> distance(int u, vector<vector<pair<int, int>>>& edge) {
        int n = edge.size();
        vector<long long> dist(n, INF);
        vector<int> mark(n);
        dist[u] = 0;
        set<pair<long long, int>> st;
        for (int i = 0; i < n; i++) {
            st.insert({dist[i], i});
        }
        while (!st.empty()) {
            auto [d, node] = *st.begin();
            st.erase(st.begin());
            mark[node] = true;
            for (auto [child, w] : edge[node]) {
                if (!mark[child]) {
                    if (dist[child] > dist[node] + w) {
                        st.erase(make_pair(dist[child], child));
                        dist[child] = dist[node] + w;
                        st.insert({dist[child], child});
                    }
                }
            }
        }
        return dist;

    }
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector<vector<pair<int, int>>> edge(n);
        vector<vector<pair<int, int>>> revEdge(n);
        for (vector<int>& e : edges) {
            edge[e[0]].emplace_back(e[1], e[2]);
            revEdge[e[1]].emplace_back(e[0], e[2]);
        }
        long long sol = INF;
        vector<long long> dist1 = distance(src1, edge);
        vector<long long> dist2 = distance(src2, edge);
        vector<long long> dist3 = distance(dest, revEdge);
        for (int i = 0; i < n; i++) {
            sol = min(sol, dist1[i] + dist2[i] + dist3[i]);
        }
        return sol >= INF ? -1 : sol;

    }
};
