//Applying Dijkstra algorithm three times
//1. First Dijkstra algorthm by taking src1 as source node and finding out the shortest distance of every node from src1 in the given graph
//2. Second Dijkstra alogorithm by taking src2 as source node and finding out the shortest distance of every node from src2 in the given graph
//3. Third Dijkstra alogorithm by taking dest as source node and finding out the shortest distance of every node from dest in the new graph formed by reversing the directions of the directed edges

//And now to find out the shortest distance of src1 and src2 from dest
//By considering that path from src1 and src2 may meet at some common node and then reach dest
//There might be a case where there is no common node and paths from sr1 and src2 are completley independant of each other so in this the common node would only be dest itself
//By considering all the combinations of taking this x node we try to find out what would the total distance from src1 to x + src2 to x + dest to x will look and if it is smaller than what we have already formed we will update our min len variable

//there might be a case when there is no route to reach dest to src1 or src2 or both in that case we will return -1

//else we will return the min len

using ll = long long;
class Solution {
public:
    void dijkstra(vector<ll> &dist, int src, list<pair<int, int>> *l) {
        set<pair<ll, ll>> s;
        s.insert({dist[src], src});

        while(!s.empty()) {
            auto it = s.begin();
            pair<ll, ll> w_n = *it;
            s.erase(s.begin());
            ll node = w_n.second;
            ll d = w_n.first;
            for(auto p: l[node]) {
               if(dist[p.first] > d + p.second) {
                   auto it = s.find({dist[p.first], p.first});
                    if(it != s.end()) s.erase(it);
                    dist[p.first] = d + p.second;
                    s.insert({dist[p.first], p.first});
                }
            }
        }
        return;

    }
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {

        long long max_val = 10000000000;

        vector<ll> dist1(n, max_val);
        vector<ll> dist2(n, max_val);
        vector<ll> dist3(n, max_val);

        list<pair<int, int>> *l = new list<pair<int, int>>[n];
        list<pair<int, int>> *dd = new list<pair<int, int>>[n];

        for(int i = 0; i < edges.size(); i++) {
            l[edges[i][0]].push_back({edges[i][1], edges[i][2]});
            dd[edges[i][1]].push_back({edges[i][0], edges[i][2]});
        }
        dist1[src1] = 0;
        dist2[src2] = 0;
        dist3[dest] = 0;

        dijkstra(dist1, src1, l);
        dijkstra(dist2, src2, l);
        dijkstra(dist3, dest, dd);

        long long  min_len = LLONG_MAX;

        if(dist3[src1] == max_val || dist3[src2] == max_val) return -1;

        for(int i = 0; i < n; i++) {
            long long len = dist1[i] + dist2[i] + dist3[i];
            min_len = min(min_len, len); 
        }

        return min_len;
    }
};
