/*           [src1] --------->[middle]<------[dest]
                              |
             [src2]-----------^

            we find mimimum cost by letting every node as middle node

            BIG Q : why we need reverse graph ?
            Ans   : Because we want to know minimum cost from middle to destination.
                    if we did't reverse graph then we have to call dijkstra from each node
                    and it is not good for time as well as memory

                    By reversing the graph by one call dijkstra from dest to all other node we are
                    able to find minimum cost to reach that node
*/
#include<bits/stdc++.h>
using namespace std;

using ll = long long;
const long long inf = 1e12;
const int mx = 1e5;

class Solution {
private:
    void dijkstra(vector<vector<pair<ll, ll>>> &a, int s, vector<ll> &d, vector<int> &vis) {
        d[s] = 0;
        priority_queue<pair<ll, ll>, vector<pair<ll, ll>>, greater<pair<ll, ll>>> pq;
        pq.push({0, s});

        while (!pq.empty()) {
            ll ver = pq.top().second;
            ll wt = pq.top().first;
            pq.pop();

            if (vis[ver]) continue;
            vis[ver] = 1;

            for (auto u : a[ver]) {
                ll to = u.first;
                ll w = u.second;

                if (d[ver] + w < d[to]) {
                    d[to] = d[ver] + w;
                    pq.push({d[to], to});
                }
            }
        }
    }
public:
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        // Create adjacency matrix for the given graph
        if(edges.size() == 0) return -1;

        vector<vector<pair<ll, ll>>> adj(n), rev(n);

        for (int i = 0; i < edges.size(); i++) {
            adj[edges[i][0]].push_back({edges[i][1], edges[i][2]});
            rev[edges[i][1]].push_back({edges[i][0], edges[i][2]});
        }

        // d1 : from src1 to other node
        vector<ll> d1(n, inf);
        vector<int> vis(n, 0);

        dijkstra(adj, src1, d1, vis);

        // d2 : from src2 to other node
        vector<ll> d2(n, inf);
        vis.assign(n, 0);
        dijkstra(adj, src2, d2, vis);

        // from dest to other vertex (reversing the edges) 
        vector<ll> d3(n, inf);
        vis.assign(n, 0);
        dijkstra(rev, dest, d3, vis);


