            ll wt = pq.top().first;
            pq.pop();

            if (vis[ver]) continue;
            vis[ver] = 1;

            for (auto u : a[ver]) {
                ll to = u.first;
                ll w = u.second;

                if (d[ver] + w < d[to]) {
                    d[to] = d[ver] + w;
                    pq.push({d[to], to});
