class Solution {
    int n;

    public long minimumWeight(int n, int[][] edges, int src1, int src2, int dest) {
        this.n = n;
        // Dijkstra will find shortest path for all nodes in graph
        // do Dijkstra for src1, src2 and dest, then find the node with shortest distance to all 3 nodes
        List<int[]>[] graph = new ArrayList[n];
        // for dest to be able to reach others since this is a directed graph
        List<int[]>[] reverseGraph = new ArrayList[n];

        for(int[] edge: edges) {
            int from = edge[0];
            int to = edge[1];
            int cost = edge[2];

            if(graph[from] == null) graph[from] = new ArrayList<int[]>();
            graph[from].add(new int[]{to, cost});

            if(reverseGraph[to] == null) reverseGraph[to] = new ArrayList<int[]>();
            reverseGraph[to].add(new int[]{from, cost});
        }

        Long[] dist1 = dijkstra(graph, src1);
        Long[] dist2 = dijkstra(graph, src2);
        Long[] dist3 = dijkstra(reverseGraph, dest);

        Long minLen = null;

        for(int i = 0; i < n; i++) {
            if(dist1[i] != null && dist2[i] != null && dist3[i] != null) {
                long sum = dist1[i] + dist2[i] + dist3[i];
                if(minLen == null) minLen = sum;
                minLen = Math.min(minLen, sum);
            }
        }

        return minLen == null ? -1: minLen;
    }

    class NodeDist{
        int node;
        long dist;

        NodeDist(int node, long dist) {
            this.node = node;
            this.dist = dist;
        }
    }


    private Long[] dijkstra(List<int[]>[] graph, int start) {
        Long[] allDist = new Long[n];
        //item[0] stores node, item[1] stores distance from start
        PriorityQueue<NodeDist> q = new PriorityQueue<NodeDist>((a,b)->{
            long res = a.dist-b.dist;
            if(res < 0) return -1;
            return 1;
        });
        q.offer(new NodeDist(start, 0));

        while(!q.isEmpty()) {
            NodeDist cur = q.poll();
            int node = cur.node;
            long dist = cur.dist;

            if(allDist[node] != null && allDist[node] < dist) continue;
            allDist[node] = dist;

            List<int[]> adjNodes = graph[node];
            if(adjNodes == null) continue;

            for(int[] adjNode: adjNodes) {
                int newNode = adjNode[0];
                int newNodeCost = adjNode[1];

                long newDist = dist + newNodeCost;

                if(allDist[newNode] == null || newDist < allDist[newNode]) {
                    allDist[newNode] = newDist;
                    q.offer(new NodeDist(newNode, newDist));
                }
            }
        }

        return allDist;
    } 
}
