class Solution {
    public long INFINITY = Long.MAX_VALUE;
    class Node {
        public int id;
        public long fromS1, fromS2, fromS1S2;
        public List<Node> toList;
        public List<Integer> weights;
        public Node(int id) {
            this.id=id;
            fromS1=fromS2=fromS1S2=INFINITY;
            this.toList=new ArrayList<>();
            this.weights=new ArrayList<>();
        }
        public void add(Node n, int weight) {
            this.toList.add(n);
            this.weights.add(weight);
        }
        public boolean updateDist(Node pNode, int weight) { // pNode->curNode
            boolean isAnyUpdate=false;
            if(pNode.fromS1!=INFINITY && this.fromS1>pNode.fromS1+weight){
                this.fromS1 = pNode.fromS1+weight;
                isAnyUpdate = true;
            } 
            if(pNode.fromS2!=INFINITY&&this.fromS2>pNode.fromS2+weight) {
                this.fromS2 = pNode.fromS2+weight;
                isAnyUpdate = true;
            }
            if(pNode.fromS1S2!=INFINITY && this.fromS1S2> pNode.fromS1S2+weight) {
                this.fromS1S2 = pNode.fromS1S2+weight;
                isAnyUpdate = true;
            }
            if(this.fromS1!=INFINITY&&this.fromS2!=INFINITY && this.fromS1S2 > this.fromS1+this.fromS2) {
                this.fromS1S2 = this.fromS1+this.fromS2;
                isAnyUpdate = true;
            }
            return isAnyUpdate;
        }
    }
    public long minimumWeight(int n, int[][] edges, int src1, int src2, int dest) {
        Node[] nodes = new Node[n];
        for(int i=0; i<n; i++) nodes[i] = new Node(i);
        nodes[src1].fromS1=0;
        nodes[src2].fromS2=0;
        Arrays.sort(edges, new Comparator<int[]>(){
            public int compare(int[] a, int[] b) {
                if(a[0]!=b[0]) return a[0]-b[0];
                if(a[1]!=b[1]) return a[1]-b[1];
                if(a[2]!=b[2]) return a[2]-b[2];
                return -1;
            }
        });
        Set<String> set = new HashSet<>();
        for(int[]edge:edges){
            if(set.contains(edge[0]+"_"+edge[1]))continue;
            nodes[edge[0]].add(nodes[edge[1]], edge[2]);
            set.add(edge[0]+"_"+edge[1]);
        }
        LinkedList<Node> queue = new LinkedList<>();
        queue.add(nodes[src1]);
        queue.add(nodes[src2]);
        boolean isAnyUpdate = true;
        while(isAnyUpdate) {
            isAnyUpdate = false;
            int curSize = queue.size();
            while(curSize--!=0) {
                Node node = queue.poll();
                for(int i = 0; i<node.toList.size(); i++) {
                    queue.offer(node.toList.get(i));
                    if(node.toList.get(i).updateDist(node, node.weights.get(i))){
                        isAnyUpdate=true;
                    }
                }
            }
        }
        return nodes[dest].fromS1S2==INFINITY?-1:nodes[dest].fromS1S2;
    }
}

