class Solution {
public:
    vector<long long> dijkstra(int n, vector<vector<pair<int, int>>> &adj, int s)
    {
        vector<long long> weight(n, LLONG_MAX);
        priority_queue<pair<long long, int>, vector<pair<long long, int>>, greater<pair<long long, int>>> pq;
        pq.push({0, s});
        weight[s] = 0;
        while(!pq.empty()){
            long long prevDis = pq.top().first;
            int node = pq.top().second;
            pq.pop();
            for(auto &it : adj[node]){
                int adjNode = it.first;
                int adjDis = it.second;
                if(weight[adjNode] > prevDis + adjDis){
                    weight[adjNode] = prevDis + adjDis;
                    pq.push({prevDis + adjDis, adjNode});
                }
            }
        }
        return weight;
    }
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector<vector<pair<int, int>>> adj(n);
        vector<vector<pair<int, int>>> adjReverse(n);
        for(auto it : edges){
            adj[it[0]].push_back({it[1], it[2]});  // norrmal adjacency list
            adjReverse[it[1]].push_back({it[0], it[2]});  // reversed adjacency list
        }

        vector<long long> d1 = dijkstra(n, adj, src1);
        vector<long long> d2 = dijkstra(n, adj, src2);
        vector<long long> d3 = dijkstra(n, adjReverse, dest);

        long long minDis = LLONG_MAX;
        for(int i = 0; i < n; i++){
            if(d1[i] == LLONG_MAX || d2[i] == LLONG_MAX || d3[i] == LLONG_MAX) continue;
            minDis = min(minDis, d1[i] + d2[i] + d3[i]);
        }
        if(minDis == LLONG_MAX) return -1;
        return minDis;
    }
};
