class Solution:
    def minimumWeight(self, n: int, edges: List[List[int]], src1: int, src2: int, dest: int) -> int:
        (self.graph, self.path_cost) = self.build_graph(edges)
        src1_paths = self.find_paths(src1, dest, self.graph)
        src2_paths = self.find_paths(src2, dest, self.graph)

        result = 10000000000
        for p1 in src1_paths:
            for p2 in src2_paths:
                result = min(result, self.calc_path_cost(p1, p2))
        return -1 if result == 10000000000 else result

    def build_graph(self, edges):
        self.graph = {}
        self.path_cost = {}
        for edge in edges:
            if edge[0] not in self.graph:
                self.graph[edge[0]] = []
            self.graph[edge[0]].append(edge[1])

            if (edge[0], edge[1]) not in self.path_cost:
                self.path_cost[(edge[0], edge[1])] = edge[2]
            else:
                self.path_cost[(edge[0], edge[1])] = min(edge[2], self.path_cost[(edge[0], edge[1])])

        return (self.graph, self.path_cost)

    def find_paths(self, src, dest, graph):
        q = [[src, (1<<src), [src]]]
        result = []
        while q:
            item = q.pop(0)
            nd = item[0]
            visited = item[1]
            path = item[2]

            if nd == dest:
                result.append(path)

            if nd not in self.graph:
                continue

            for nb in self.graph[nd]:
                if (1<<nb) & visited:
                    continue
                new_path = copy.copy(path)
                new_path.append(nb)
                q.append([nb, (1<<nb) | visited, new_path])
        return result

    def calc_path_cost(self, p1, p2):
        cost = 0

        N = len(p1)
        visited = set()
        for i in range(1, N):
            edge = (p1[i-1], p1[i])
            cost += self.path_cost[edge]
            visited.add(edge)

        N = len(p2)
        for i in range(1, N):
            edge = (p2[i-1], p2[i])
            if edge in visited:
                continue
            cost += self.path_cost[edge]
        return cost
