Thanks to this original solution, [Three Dijkstras](https://leetcode.com/problems/minimum-weighted-subgraph-with-the-required-paths/discuss/1844095/Three-Dijkstras), that explained the logic.
More about [Dijkstra's algorithm](https://en.wikipedia.org/wiki/Dijkstra%27s_algorithm).

## Priority Queue
```
const MAX = int64(10000000000)

type pair struct {
    key int64
    value int
}

type Item struct {
	value pair
	priority int
	index int
}

type PriorityQueue []*Item

func (pq PriorityQueue) Len() int { return len(pq) }

func (pq PriorityQueue) Less(i, j int) bool {
    if pq[i] == nil || pq[j] == nil {
        return false
    }
    return pq[i].priority > pq[j].priority
}

func (pq PriorityQueue) Swap(i, j int) {
    if pq[i] != nil && pq[j] != nil {
        pq[i], pq[j] = pq[j], pq[i]
        pq[i].index = i
        pq[j].index = j
    }
}

func (pq *PriorityQueue) Push(x interface{}) {
	n := len(*pq)
	item := x.(*Item)
	item.index = n
	*pq = append(*pq, item)
}

func (pq *PriorityQueue) Pop() interface{} {
	old := *pq
	n := len(old)
	item := old[n-1]
	old[n-1] = nil
    if item != nil {
        item.index = -1
    }
	*pq = old[0 : n-1]
	return item
}

func (pq *PriorityQueue) update(item *Item, value pair, priority int) {
	item.value = value
	item.priority = priority
	heap.Fix(pq, item.index)
}

func min(a, b int64) int64 {
    if a < b {
        return a
    }
    return b
}

func dijkstras(node int, g [][]pair, visited *[]int64) {
    pq := make(PriorityQueue, len(*visited))
    pq[0] = &Item{ value: pair{0, node} }
    heap.Init(&pq)

    for pq.Len() > 0 {
        item := heap.Pop(&pq).(*Item)
        if item != nil {
            if (*visited)[item.value.value] == item.value.key {
                for _, c := range g[item.value.value] {
                    if (*visited)[c.key] > item.value.key + int64(c.value) {
                        (*visited)[c.key] = item.value.key + int64(c.value)
                        heap.Push(&pq, &Item{ value: pair{(*visited)[int(c.key)], int(c.key)} })
                    }
                }
            }
        }
    }
}

func minimumWeight(n int, edges [][]int, src1 int, src2 int, dest int) int64 {
    result := MAX
    g, rg := make([][]pair, n), make([][]pair, n)
    l1, l2, ld := make([]int64, n), make([]int64, n), make([]int64, n)
    for i := 0; i < n; i++ {
        l1[i], l2[i], ld[i] = MAX, MAX, MAX
    }
    l1[src1], l2[src2], ld[dest] = 0, 0, 0

    for _, edge := range edges {
        g[edge[0]] = append(g[edge[0]], pair{int64(edge[1]), edge[2]})
        rg[edge[1]] = append(rg[edge[1]], pair{int64(edge[0]), edge[2]})
    }

    dijkstras(src1, g, &l1)
    dijkstras(src2, g, &l2)
    dijkstras(dest, rg, &ld)

    if ld[src1] == MAX || ld[src2] == MAX {
        return -1
    }
    for i := 0; i < n; i++ {
        result = min(result, ld[i] + l1[i] + l2[i])
    }
    return result
}
```

