class Solution {
    public long minimumWeight(int n, int[][] edges, int src1, int src2, int dest) {
        /* 
            For finding the minmum weighted subgraph, there will be a node from the src1 and src2,
            after which the path from that node to the destination will be same 
        */

        HashMap<Integer, List<Edge>> map = new HashMap<>();
        HashMap<Integer, List<Edge>> revMap = new HashMap<>();
        for(int[] e : edges){
            map.computeIfAbsent(e[0], k -> new ArrayList<>()).add(new Edge(e[1], e[2]));
            revMap.computeIfAbsent(e[1], k -> new ArrayList<>()).add(new Edge(e[0], e[2]));
        }


        long[] dis1 = new long[n]; // From src1
        long[] dis2 = new long[n]; // From src2 
        long[] dis3 = new long[n]; // From dest

        findShortestDistance(src1, dis1, map);
        findShortestDistance(src2, dis2, map);
        findShortestDistance(dest, dis3, revMap);

        long ans = Long.MAX_VALUE;

        for(int i=0; i<n; i++){
            if(dis1[i] != Long.MAX_VALUE && dis2[i] != Long.MAX_VALUE && dis3[i] != Long.MAX_VALUE){
                long t = dis1[i] + dis2[i] + dis3[i];
                ans = Math.min(ans, t);
            }
        }

        return ans == Long.MAX_VALUE ? -1 : ans;
    }

    // Dijikstra
    private void findShortestDistance(int source, long[] dist, HashMap<Integer, List<Edge>> map){
        Arrays.fill(dist, Long.MAX_VALUE);
        dist[source] = 0;

        // Min Heap
        PriorityQueue<Edge> pq = new PriorityQueue<>((a, b) -> Long.compare(a.weight, b.weight));
        pq.offer(new Edge(source, 0));


        while(!pq.isEmpty()){
            Edge ed = pq.poll();

            if(ed.weight > dist[ed.node]) continue;

            // Check if there are neighbors
            if(!map.containsKey(ed.node)) continue;

            for(Edge neigh : map.get(ed.node)){
                if(dist[ed.node] + neigh.weight < dist[neigh.node]){
                    dist[neigh.node] = dist[ed.node] + neigh.weight;
                    pq.offer(new Edge(neigh.node, dist[neigh.node]));
                }
            }
        }

        return;
    }

    private class Edge{
        public int node;
        public long weight;

        public Edge(int node, long weight){
            this.node = node;
            this.weight = weight;
        }
    }
