class Solution {
  private static final long INF = Long.MAX_VALUE >> 1;

  public long minimumWeight(int n, int[][] edges, int src1, int src2, int dest) {
    List<int[]>[] graph = new List[n];
    List<int[]>[] revGraph = new List[n];
    for (int i = 0; i < n; i++) {
      graph[i] = new ArrayList<>();
      revGraph[i] = new ArrayList<>();
    }
    for (var edge : edges) {
      int u = edge[0], v = edge[1], w = edge[2];
      graph[u].add(new int[] {v, w});
      revGraph[v].add(new int[] {u, w});
    }

    long[] dist1 = dijkstra(graph, src1);
    long[] dist2 = dijkstra(graph, src2);
    long[] dist3 = dijkstra(revGraph, dest);

    long ans = INF;

    for (int i = 0; i < n; i++) {
      if (dist1[i] == INF || dist2[i] == INF || dist3[i] == INF) continue;

      ans = Math.min(ans, dist1[i] + dist2[i] + dist3[i]);
    }

    return ans == INF ? -1 : ans;
  }

  private long[] dijkstra(List<int[]>[] graph, int start) {
    int n = graph.length;

    // [node(int), dist(long)]
    Queue<long[]> queue = new PriorityQueue<>((a, b) -> Long.compare(a[1], b[1]));
    long[] dist = new long[n];
    Arrays.fill(dist, INF);

    dist[start] = 0;
    queue.add(new long[] {start, dist[start]});

    while (!queue.isEmpty()) {
      long[] curr = queue.poll();
      int u = (int) curr[0];
      long d = curr[1];

      if (d != dist[u]) continue;

      for (int[] next : graph[u]) {
        int v = next[0], w = next[1];

        if (dist[u] + w < dist[v]) {
          dist[v] = dist[u] + w;
          queue.add(new long[] {v, dist[v]});
        }
      }
    }

    return dist;
  }
}
