class Solution {
    vector<long long> dij(int n, vector<vector<pair<int,int>>>&graph,int source) {
        vector<long long>dist(n,LLONG_MAX); 
        dist[source] = 0; 
        priority_queue<pair<long long, long long>, vector<pair<long long, long long>> , greater<pair<long long, long long>>>pq; 
        pq.push({source, 0}); 
        while (!pq.empty()) {
            auto [node, di] = pq.top(); pq.pop(); 
            if (dist[node] < di ) continue; 
            for (auto [child, cost] : graph[node]) {
                if (dist[child] > dist[node] + cost) {
                    dist[child] = dist[node] + cost; 
                    pq.push({child, dist[child]}); 
                }
            }
        }
        return dist; 
    }
public:
    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector<vector<pair<int,int>>>graph(n), reverse_graph(n); 
        for (auto&itr:edges) {
            graph[itr[0]].push_back({itr[1], itr[2]}); 
            reverse_graph[itr[1]].push_back({itr[0],itr[2]}); 
        }
        vector<long long>fromS1 = dij(n,graph, src1); 
        vector<long long>fromS2 = dij(n,graph, src2); 
        vector<long long>fromDest = dij(n,reverse_graph, dest); 
        long long ans {LLONG_MAX}; 
        for (int i=0; i<n; ++i) {
            if (fromS1[i]==LLONG_MAX || fromS2[i] == LLONG_MAX || fromDest[i] == LLONG_MAX) continue; 
            ans = min(ans , fromS1[i] + fromS2[i] + fromDest[i]); 
        }
        return ans == LLONG_MAX ? -1 : ans; 
    }
};


