class Solution {
public:
    #define ll long long int
    const ll mx = 1e18;

    vector<ll> dijkstra(ll src, ll n , vector<pair<ll,ll>>adj[]){
        vector<ll>dis(n,mx);
        dis[src]=0;
        priority_queue<pair<ll,ll>, vector<pair<ll,ll>> , greater<pair<ll,ll>> >q;
        q.push({0,src});
        while(q.size()>0){
            ll a= q.top().first; // distance
            ll b =q.top().second; // node
            q.pop();

            if (a != dis[b]) continue; // must , otherwise give TLE

            vector<pair<ll,ll>>:: iterator j;

            for(auto j:adj[b]){
                if(dis[j.first]> dis[b]+ j.second){
                    dis[j.first]= dis[b]+j.second;
                    q.push({dis[j.first] ,j.first});
                }
            }
        }

        return dis;
    }

    long long minimumWeight(int n, vector<vector<int>>& edges, int src1, int src2, int dest) {
        vector<pair<ll,ll>>adj[n];
        vector<pair<ll,ll>>reversed[n];

        for(int i=0;i<edges.size();i++){
            int u =edges[i][0] , v=edges[i][1] , w=edges[i][2];
            adj[u].push_back({v,w});
            reversed[v].push_back({u,w});
        }

        vector<ll>src1v = dijkstra(src1,n,adj); 
        vector<ll>src2v = dijkstra(src2,n,adj);

        // here need distance from each vertex to dest , so we used reversed vector;
        vector<ll>destv = dijkstra(dest,n,reversed);;

        ll c = mx;
        for(int i=0;i<n;i++){
                //ll temp =dijkstra(src1,i ,n , adj) + dijkstra(src2,i ,n , adj) 
                //         + dijkstra(i,dest ,n , adj);
                ll temp = src1v[i]+src2v[i]+destv[i];
                c=min(c,temp);
        }

        if(c>=mx)
            c=-1;

        return c;
    }
};
