class Solution {
    ArrayList<int[]>[] forwardGraph, backGraph;

    public long minimumWeight(int n, int[][] edges, int src1, int src2, int dest) {
        forwardGraph = new ArrayList[n];
        backGraph= new ArrayList[n];
        for (int i = 0; i < n; i++) {
            forwardGraph[i] = new ArrayList<int[]>();
            backGraph[i] = new ArrayList<int[]>();
        }
        for (int[] e:  edges){
            int from = e[0];
            int to = e[1];
            int w = e[2];
            forwardGraph[from].add(new int[]{to, w});
            backGraph[to].add(new int[]{from, w});
        }

        long[] path1 = new long[n]; //from src1 to all nodes
        long[] path2 = new long[n]; //from src2 to all nodes
        long[] path3 = new long[n]; //from dest to all nodes
        runShortest(path1, src1, forwardGraph);
        runShortest(path2, src2, forwardGraph);
        runShortest(path3, dest, backGraph);

        long result = -1;
        for (int i = 0; i < n; i++) {
            long c1 = path1[i];
            long c2 = path2[i];
            long c3 = path3[i];
            if (c1 != -1 && c2 != -1 && c3 != -1) {
                long c = c1 + c2 + c3;
                if (result == -1 ||  c < result) {
                    result = c;
                }
            }
        }
        return result;
    }

    public void runShortest(long[] distance, int source, ArrayList<int[]>[] AdjList) {
        PriorityQueue<long[]> q = new PriorityQueue<long[]>(Comparator.comparingLong(a->a[1]));
        for (int[] next : AdjList[source]) {
            q.offer(new long[]{next[0], next[1]});
        }
        Arrays.fill(distance, -1);
        distance[source] = 0;
        while (!q.isEmpty()) {
            long[] curr = q.poll();
            int currNode = (int) curr[0];
            long currDistance = curr[1];
            if (distance[currNode] != -1 && distance[currNode] <= currDistance) continue;
            distance[currNode]  = currDistance;
            for (int[] next: AdjList[currNode]) {
                int nextNode = next[0];
                int weight = next[1];
                q.add(new long[]{(long)nextNode, currDistance + weight});
            }
        }
    }
}

