class Solution {
    public long minimumWeight(int n, int[][] edges, int src1, int src2, int dest) {

        Map<Integer, Set<Path>> map = new HashMap<>();
        Map<Integer, Set<Path>> reverseMap = new HashMap<>();

        for (int[] edge: edges) {
            int from = edge[0];
            int to = edge[1];
            int weight = edge[2];

            if (!map.containsKey(from)) {
                map.put(from, new HashSet<>());
            }

            if (!reverseMap.containsKey(to)) {
                reverseMap.put(to, new HashSet<>());
            }

            map.get(from).add(new Path(from, to, weight));
            reverseMap.get(to).add(new Path(to, from, weight));
        }

        Map<Integer, Long> srcDistanceMap1 = new HashMap<>();
        Map<Integer, Long> srcDistanceMap2 = new HashMap<>();
        Map<Integer, Long> dstDistanceMap = new HashMap<>();

        search(map, src1, srcDistanceMap1);
        search(map, src2, srcDistanceMap2);
        search(reverseMap, dest, dstDistanceMap);

        if (!srcDistanceMap1.containsKey(dest) || !srcDistanceMap2.containsKey(dest)) {
            return -1;
        }

        long res = Long.MAX_VALUE;

        for (int node: dstDistanceMap.keySet()) {
            if (!srcDistanceMap1.containsKey(node) || !srcDistanceMap2.containsKey(node)) {
                continue;
            }

            res = Math.min(res, dstDistanceMap.get(node) + srcDistanceMap1.get(node) + srcDistanceMap2.get(node));

        }

        return res;
    }

    private void search(Map<Integer, Set<Path>> map, int src, Map<Integer, Long> distanceMap) {
        distanceMap.put(src, 0L);

        Set<Integer> visited = new HashSet<>();
        visited.add(src);

        PriorityQueue<Path> pq = new PriorityQueue<>((p1, p2) -> {
            return (int)((distanceMap.get(p1.from) + p1.weight) - (distanceMap.get(p2.from) + p2.weight));
        });

        for (Path path: map.getOrDefault(src, new HashSet<>())) {
            pq.add(path);
        }

        while (!pq.isEmpty()) {
            Path cur = pq.poll();

            if (visited.contains(cur.to)) {
                continue;
            }
            visited.add(cur.to);

            distanceMap.put(cur.to, distanceMap.get(cur.from) + cur.weight);

            for (Path path: map.getOrDefault(cur.to, new HashSet<>())) {
                pq.add(path);
            }
        }

