class Solution {
    public long minimalKSum(int[] nums, int k) {
        long res = (long)(1+k)*k/2;
        Set<Integer> set = new HashSet<>();
        for(int num: nums){
            set.add(num);
        }
        int next = k+1;
        for(int num: set){
            if(num <= k){
                while(set.contains(next)){ //Check N times at most for double loop
                    next++;
                }
                res+=next-num;
                next++;
            }
        }
        return res;
    }
}
