class Solution {
    public long minimalKSum(int[] nums, int k) {
        TreeSet<Long> ts = new TreeSet<>();
        for(int num:nums)
            ts.add((long)num);

        long prev = 0;
        long sum = 0;
        for(long num: ts){
            if(num==1)
            {
                prev = num;
                continue;
            }

            if((num)-prev>k){
              num = prev+k+1;  
            }
            k -= ((num-1)-prev);
            sum+= ((num-1)*(num)/2)-(prev*(prev+1)/2);
            if(k==0)
                return sum;

            prev = num;
        }

        if(k!=0){
        long upper = prev+k+1; 
        sum+=    (upper*(upper-1)/2)-(prev*(prev+1)/2);
        }
       return sum;     
    }
}

