- Find a numver x using binary search such that there are y less numbers in nums so that y + k = x;
- then x *(x+1)/2 - sum of y numbers is the ans
```
class Solution {
public:
    int find(int x, vector<int>& nums) {
        int l = 0, r = nums.size();
        while(l < r) {
            int m = l + (r-l)/2;
            if(nums[m] <= x) {
                l = m+1;
            } else {
                r = m;
            }
        }
        return r;
    }
    long long minimalKSum(vector<int>& nums, int k) {
        long long ret = 0;
        vector<int> v;
		// sort the numbers so that we can binary search
        sort(nums.begin(), nums.end());

		// remove repeating numbers
        v.push_back(nums[0]);
        for(int i = 1; i < nums.size(); i++) {
            if(nums[i] != nums[i-1]) v.push_back(nums[i]);
        }

		// binary search
        long long l = 1, r = 10000000000;
        int count = 0;
        while(l < r) {
            long long m = l + (r-l)/2;
			// binary search on nums to find count of numbers less than mid
            count = find(m, v);
            if(m - count < k) {
                l = m + 1;
            } else {
                r = m;
            }
        }

		// find ans using sum of n numbers by reducing the numbers present in nums
        for(int i = 0; i < count; i++) ret -= v[i];
        ret += r * (r+1) /2;

        return ret;
    }
};
```

