class Solution {
    public long minimalKSum(int[] nums, int k) {
        Arrays.sort(nums);

        // fill gaps
        long result = 0;
        long c = 1;
        for (int i : nums) {
            if (i > c) {
                while (c < i && k > 0) {
                    result += c;
                    c++;
                    k--;
                }
            }
            c = i+1;
        }

        // add the left overs
        while (k > 0) {
            result += c;
            c++;
            k--;
        }

        return result;
    }
}
