class Solution {
    public long minimalKSum(int[] nums, int k) {
        int p = 0;
        long res = (long)k*((long)k+1)/2;
        Arrays.sort(nums);
        for(int i=0 ;i<nums.length ;i++) {
            if(p != nums[i] && k >= nums[i]) {
                res -= nums[i];
                res += ++k;
            }
            else if(k < nums[i]) {
                break;
            }
            p = nums[i];
        }
        return res;
    }
}
