class Solution {
public:
    long long minimalKSum(vector<int>& nums, int k) {
        sort(begin(nums), end(nums));

        nums.push_back(2 * 1e9 + 1);
        if(nums[0] != 1){
            nums.insert(nums.begin(), 0);
        }

        long long sum = 0;
        int i = 0;

        while(k > 0 && i < nums.size() - 1){
            int n = nums[i + 1] - nums[i] - 1;
            if(n > 0){
                if(k >= n){
                    k -= n;
                    long long curr = 1LL * ((2LL * nums[i] + 1 + n) * n ) / 2;
                    sum += curr;
                }
                else{
                    long long curr = 1LL * ((2LL * nums[i] + 1 + k) * k ) / 2;
                    k = 0;
                    sum += curr;
                }
            }
            i++;
        }

        return sum;
    }
};
