class Solution:
    def minimalKSum(self, nums: List[int], k: int) -> int:
        nums = set(nums)
        res, last = k * (k + 1) // 2, k
        for num in nums:
            if num <= k:
                last += 1
                while last in nums:
                    last += 1
                res += last - num
        return res
