class Solution {
public:
    long long minimalKSum(vector<int>& nums, int k) {

        const int m = nums.size();

        unordered_set<int> filtered_nums; // unique, <= k + m
        for(auto num : nums)
        {
            if(num <= k + m)
            {
                filtered_nums.insert(num);
            }
        }

        const int64_t sum_filtered = accumulate(filtered_nums.begin(), filtered_nums.end(), 0L);
        const int n_filtered = filtered_nums.size();

        int x = k + m;
        int64_t sum = static_cast<int64_t>(x) * (x + 1) / 2  -  sum_filtered;

        int count = m - n_filtered; 
            // we need to remove this many numbers from [1, k+m] \ filtered_nums
        while(count > 0)
        {
            if(filtered_nums.count(x) == 0)
            {
                --count, sum -= x;
            }

            --x;
        }

        return sum;
    }
};
