    public static long minimalKSum(int[] nums, int k) {
        Arrays.sort(nums);
        int [] arr = new int [nums.length+2];
        arr[0] = 0;
        arr[arr.length - 1] = nums[nums.length - 1] + k + 1;
        System.arraycopy(nums, 0, arr, 1, nums.length);
        long sum = 0;

        for (int i = 1; i < arr.length && k > 0; i++) {
            int j = i-1;
            if(arr[i] == arr[j]) continue;
            long take = Math.min(arr[i] - (arr[j] + 1), k), n = arr[j] + take;
            sum = sum + (n * (n+1)/2) - (arr[j] * ((long)arr[j] + 1)/2);
		    //1 + 2 + ... + n - ( 1 + 2 + ... + x ) == x + 1 + x + 2 + ... + n, n > x
			// =  n(n+1)/2 - x(x+1)/2
            k -= take;
        }

        return sum;
    }
