class Solution {
public:
    long long minimalKSum(vector<int>& nums, int k) {

        // Dry Run on algorithm: 

        // nums = [1,4,25,10,25]

        // Firstly, we will sort nums
        // nums = [1,4,10,25,25]

        // i = 0 and j = 0 (nums[j]=1)
        // Therefore, we have no digits in between 'i' and nums[j]
        // so i = nums[j], j+=1 and sum = 0

        // Now, i = 1 and j = 1 (nums[j]=4)
        // For i =1 and nums[j]=4 
        // we have two digits to insert (2 and 3) 
        // so sum of 2 and 3
        // is sum of Arithmatic progression 
        // with first term, a= i+1 = 2
        // common difference, d = 1
        // no. of terms, x= nums[j]-i-1 = 2 
        // if k is less than x, we will set x = k (decrement k, k-=x)
        // sum+= (x*(2*a+x-1))/2 = 5

        // we will repeat the step until k is not 0




        sort(nums.begin(),nums.end());

        long long sum=0;
        long long i = 0;
        long long j = 0; // index
        int n = nums.size();

        // For this question we will use sum of arithmatic progression
        // if first no. 'a' and comman diff is 'd'
        // and we want sum of 'n' numbers
        // sum = n/2(2*a+(n-1)*d)
        while(k)
        {       

            if(j>=n)
                break;
            if(i==nums[j]) 
            {
                i=nums[j];
                j+=1;
            }

            else
            {   
                // x = no. of digits not in array
                // between nums[j] and i
                long long x = (nums[j]-i-1); 

                // if k is less than x
                // we set x = k
                if(k<(nums[j]-i-1))
                     x=k;

                long a = i+1; // first term 'a'

                sum+=(x*(2*a+x-1))/2; // sum of AP
                k-=x; // remove x terms
                i = nums[j]; // move to next element available in nums
                j+=1; // increment index
            }


        }

        // if we have reached end of nums
        // and still we need to add sum k
        if(k>0)
        {
            long long x = k; // no. of terms
            long a = i+1; // first term
            sum+=(x*(2*a+x-1))/2; // sum of AP
        }

        return sum;

    }
};

