class Solution {
    public long minimalKSum(int[] nums, long k) {
        Arrays.sort(nums);  // sort the array
        Set<Integer> set = new HashSet<>(); // used to keep track of repeating number
        long ans = k*(k+1)/2;   // we assume our answer is sum of  1,2,3,4,5......(k-1),k.
        for(int num : nums) {
            if(num<=k) {    // if we added "num" in our answer then , we subtract and add next (k+1) value in answer
                if(set.contains(num)) continue; // if we found num previously then continue
                set.add(num);
                ans-=num;
                k++;
                ans+=k;
            } else {
                break;
            }
        }
        return ans;
    }
}
