class Solution {
    public long minimalKSum(int[] nums, int k) {
        Arrays.sort(nums);

        // Find the first k unique numbers sum
        long ans = 0;
        int i = 0;
        long prev = 0;
        while(i < nums.length){
            if(k == 0) break;
            if(i >= 1 && nums[i] == nums[i-1]){
                i++;
                continue;
            }

            long numAval = nums[i] - prev - 1;
            long reqNum = Math.min(numAval, k);

            // Find the sum of the required numbers
            long a = prev + reqNum;
            ans += (a)*(a+1)/2 - (prev)*(prev+1)/2;

            prev = nums[i];
            k -= reqNum;
            // System.out.println(i + " " + nums[i] + " " + ans + " " +k);
            ++i;
        }
        if(k != 0){
            long a = prev + k;
            ans += (a)*(a+1)/2 - (prev)*(prev+1)/2;
        }

        return ans;
    }
}
