        for(int i=0;i<n;i++)
        {
            if(i>0 && nums[i]==nums[i-1])
                continue;
            if(nums[i]<=e)
            {
                sum+=(e+1-nums[i]);
                e++;
            }
        }
        return sum;
    }
};
