public:
    long long give(long long n){
        long long ans=n*(n+1)/2;
        return ans;
    }
    long long minimalKSum(vector<int>& v, int k) {
        int n=v.size();
        v.push_back(0);
        sort(v.begin(),v.end());
        long long sum=0;
        for(int i=0;i<n;i++){
          int dif=v[i+1]-v[i];

            if(k==0)break;
            if(dif<=1)continue;
            dif--;
            long long take=min(k,dif);
            sum+=(give(v[i]+take)-give(v[i]));
            k-=take;
        }

        if(k!=0)sum+=(give(v[n]+k)-give(v[n]));
        return sum;
    }
};
