		public long minimalKSum(int[] nums, int k) {
			Arrays.sort(nums);
			nums = uniq(nums);

			long low = 0, high = 1100000000;
			while(high - low > 1){
				long h = high+low>>1;
				int num = lowerBound(nums, (int)h+1);
				if((long)num+k <= h){
					high = h;
				}else{
					low = h;
				}
			}

			long ans = (long)high*(high+1)/2;
			int num = lowerBound(nums, (int)high+1);
			for(int i = 0;i < num;i++){
				ans -= nums[i];
			}
			return ans;
		}

		public int lowerBound(int[] a, int v){ return lowerBound(a, 0, a.length, v); }
		public int lowerBound(int[] a, int l, int r, int v)
		{
