class Solution {
public:

    // calculates sum from 1 to A
    long long sumA(long long a){
        if(a <= 0) return 0;
        return (a * (a+1)) / 2;
    }

    // calculates sum from A to B
    long long sumAB(long long a, long long b){
        if(a>b) return 0;
        return sumA(b) - sumA(a-1);
    }

    long long minimalKSum(vector<int>& nums, int k) {
        map<int, int> mp;
        for(auto it : nums) mp[it] = 1;

        auto it  = mp.end(); it--;
        long long last = it->first;

        // adding these two element to make sure map elements always cover num
        mp[0] = 1;
        mp[last + k + 1] = 1;

        auto prev = mp.begin();
        it  = prev; it++;

        long long sum = 0;

        // iterate the map and add the difference
        while( (it != mp.end()) && (k>0) ){
            long long x = it->first - prev->first - 1;

            // if gap is greate than range requried
            if( x > k){
                sum += sumAB(prev->first + 1, prev->first + 1 + k-1);
                k = 0;
            }else{  
                sum += sumAB(prev->first + 1, it->first - 1);
                k -= x;
            }

            it++; prev++;
        }

        return sum;
    }
};
```

