class Solution {
public:
  long long minimalKSum(vector<int>& nums, int k) {
    long long ans = 0;
    nums.push_back(0);
    sort(nums.begin(), nums.end());
    int i = 0, n = nums.size();
    while (i < n - 1 && k > 0) {
      if (nums[i] == nums[i + 1] || nums[i] + 1 == nums[i + 1]) {
        i++;
        continue;
      }
      long long l = nums[i] + 1, r = nums[i + 1] - 1;
      ans += (l + min(r, l + k - 1)) * (min(r - l + 1, (long long)k)) / 2;
      k -= (r - l + 1);
      i++;
    }
    if (k > 0) {
      long long l = nums.back() + 1;
      ans += (l + l + (k - 1)) * k / 2;
    }
    return ans;
  }
};
