        long long sum=0;
        int n=nums.size();       
        sort(nums.begin(),nums.end());
        int  diff=nums[0]-1;
        if(diff>0){
            if(diff>k){
                sum+=find(k);
               k=0;
            }else{
                sum+=find(nums[0]-1);                 
                k-=diff;
