class Solution {
    public long getSum(int l,int r){
        if(l>r) return 0;
        if(l==r) return l;
        return (long)(r+l)*(r-l+1)/2;
    }
    public long minimalKSum(int[] nums, int k) {

        Arrays.sort(nums);
        int n = nums.length;
        long  ans = 0;
        int len = nums[0]-1;
        if(k>=len){
           // System.out.println(k + " " + len);
          //  System.out.println(getSum(1,nums[0]-1));
            ans +=getSum(1,nums[0]-1);
            k-=len;
        }
        else {
          //  System.out.println(k + " " + len);
          //  System.out.println(getSum(1,k));
            ans += getSum(1,k);
            k=0;
        }
        for(int i =1;i<n;i++){
            int x = nums[i];
            int y = nums[i-1];
            if(x==y) continue;
            len = x-y-1;
            if(len==0) continue;
            if(k>=len){
              //  System.out.println(k + " " + len + " " + i);
                ans += getSum(y+1,x-1);
                k-=len;

            }else {
                ans+= getSum(y+1,y+k);
                k=0;
            }
        }
       // System.out.println(k);
