class Solution {
public:
    long long minimalKSum(vector<int>& nums, int k) {
        sort(nums.begin(), nums.end());
        long long miss_sum  = 0;
        // int miss_count = 0;
        int curr = 1;  
        for (int num : nums) {
            if (num > curr) {
                int miss_range = num - curr;
                int total_ele = min(miss_range, k);
                miss_sum += (long long) total_ele *
                (curr + curr + total_ele - 1) / 2;
                k -= total_ele;
                // miss_count += total_ele;
                if (k == 0) break;
            }
            curr = num + 1;
        }
        if (k > 0)
            miss_sum += (long long) k * (curr + curr + k - 1) / 2;
        return miss_sum;
    }
};

