class Solution {
public:
    long long minimalKSum(vector<int>& nums, int k) {
        int n = nums.size();

        long long t = (long long)k;
        long long sum = (t*(t+1))/2;

        set<int> s;

        for(int i = 0; i< n; i++)
        {
            s.insert(nums[i]);
        }

        for(auto it: s)
        {
            if(it <= k)
            {
                sum -= it;
                k++;
                sum += k;
            }
        }

        return sum;
    }
};
